import React from 'react';
import { Link } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  // Reemplazar con imágenes reales si no hay una
  const productImage = product.image.startsWith('/') 
    ? `https://images.unsplash.com/photo-${Math.floor(Math.random() * 1000) + 1500000000000}-c${Math.floor(Math.random() * 1000)}?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=80`
    : product.image;
    
  return (
    <div className="product-card">
      <div className="product-badge">
        {product.isNew && <span className="badge new">Nuevo</span>}
        {product.discount > 0 && <span className="badge discount">-{product.discount}%</span>}
      </div>
      
      <div className="product-image">
        <img src={productImage} alt={product.name} />
        <div className="product-actions">
          <button className="action-btn" title="Añadir al carrito">
            <i className="fas fa-shopping-cart"></i>
          </button>
          <button className="action-btn" title="Añadir a favoritos">
            <i className="fas fa-heart"></i>
          </button>
          <Link to={`/producto/${product.id}`} className="action-btn" title="Vista rápida">
            <i className="fas fa-eye"></i>
          </Link>
        </div>
      </div>

      <div className="product-info">
        <div className="product-category">{product.category}</div>
        <h3 className="product-name">
          <Link to={`/producto/${product.id}`}>{product.name}</Link>
        </h3>
        <div className="product-rating">
          {[...Array(5)].map((_, i) => (
            <i 
              key={i} 
              className={`${i < product.rating ? 'fas' : 'far'} fa-star`}
            ></i>
          ))}
          <span className="rating-count">({product.reviewCount})</span>
        </div>
        <div className="product-price">
          {product.discount > 0 && (
            <span className="original-price">
              ${product.originalPrice.toFixed(2)}
            </span>
          )}
          <span className="current-price">
            ${product.price.toFixed(2)}
          </span>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
