import React from 'react';
import { Link } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const {
    id,
    name,
    category,
    image,
    price,
    originalPrice,
    discount,
    rating,
    reviewCount,
    isNew
  } = product;

  const renderStars = (rating) => {
    return [...Array(5)].map((_, index) => (
      <i
        key={index}
        className={`fas fa-star ${index < rating ? 'filled' : ''}`}
      ></i>
    ));
  };

  return (
    <div className="product-card">
      <div className="product-badge">
        {isNew && <span className="badge new">Nuevo</span>}
        {discount > 0 && (
          <span className="badge discount">{discount}% OFF</span>
        )}
      </div>
      <div className="product-image">
        <img src={image} alt={name} />
        <div className="product-actions">
          <button className="action-btn" title="Añadir a favoritos">
            <i className="fas fa-heart"></i>
          </button>
          <button className="action-btn" title="Vista rápida">
            <i className="fas fa-eye"></i>
          </button>
          <button className="action-btn" title="Añadir al carrito">
            <i className="fas fa-shopping-cart"></i>
          </button>
        </div>
      </div>
      <div className="product-info">
        <span className="product-category">{category}</span>
        <h3 className="product-name">
          <Link to={`/producto/${id}`}>{name}</Link>
        </h3>
        <div className="product-rating">
          {renderStars(rating)}
          <span className="rating-count">({reviewCount})</span>
        </div>
        <div className="product-price">
          {discount > 0 && (
            <span className="original-price">${originalPrice}</span>
          )}
          <span className="current-price">${price}</span>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
