import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import './ProductDetailPage.css';

const ProductDetailPage = () => {
  const { id } = useParams();
  
  // Datos de muestra para un producto (en una aplicación real, se obtendrían de una API)
  const product = {
    id: 1,
    name: 'Lámpara Artesanal de Madera',
    category: 'Iluminación',
    description: 'Lámpara artesanal hecha a mano con madera de alta calidad. Diseño único y elegante que aporta calidez a cualquier espacio. Perfecta para salas de estar, dormitorios o espacios de trabajo.',
    features: [
      'Material: Madera de roble y metal',
      'Dimensiones: 30cm x 30cm x 45cm',
      'Potencia: 60W máximo',
      'Tipo de bombilla: E27',
      'Incluye interruptor de cable'
    ],
    price: 89.99,
    originalPrice: 119.99,
    discount: 25,
    rating: 4.5,
    reviewCount: 12,
    isNew: false,
    stock: 15,
    images: [
      'https://images.unsplash.com/photo-1513506003901-1e6a229e2d15?ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80',
      'https://images.unsplash.com/photo-1494438639946-1ebd1d20bf85?ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80',
      'https://images.unsplash.com/photo-1517991104123-1d56a6e81ed9?ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80',
      'https://images.unsplash.com/photo-1507473885765-e6ed057f782c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1500&q=80'
    ],
    colors: ['Natural', 'Negro', 'Blanco'],
    sizes: []
  };

  useEffect(() => {
    // Aquí se cargaría el producto desde la API según el ID
    console.log(`Cargando producto con ID: ${id}`);
    // En una aplicación real haríamos una petición como:
    // fetch(`/api/products/${id}`).then(res => res.json()).then(data => setProduct(data));
  }, [id]);

  // Estados para gestionar la selección del usuario
  const [mainImage, setMainImage] = useState(product.images[0]);
  const [quantity, setQuantity] = useState(1);
  const [selectedColor, setSelectedColor] = useState(product.colors[0]);

  // Manejar cambio de cantidad
  const handleQuantityChange = (e) => {
    const value = parseInt(e.target.value);
    if (value > 0 && value <= product.stock) {
      setQuantity(value);
    }
  };

  // Incrementar cantidad
  const incrementQuantity = () => {
    if (quantity < product.stock) {
      setQuantity(quantity + 1);
    }
  };

  // Decrementar cantidad
  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  // Manejar el cambio de imagen principal
  const handleImageChange = (image) => {
    setMainImage(image);
  };

  // Manejar el cambio de color
  const handleColorChange = (color) => {
    setSelectedColor(color);
  };

  // Manejar la adición al carrito
  const handleAddToCart = () => {
    alert(`Producto añadido al carrito: ${quantity} unidades, color: ${selectedColor}`);
    // Aquí iría la lógica para añadir al carrito
  };

  return (
    <div className="product-detail-page">
      <Navbar />

      <div className="container">
        <div className="product-detail">
          <div className="product-images">
            <div className="main-image">
              <img src={mainImage} alt={product.name} />
            </div>
            <div className="thumbnail-images">
              {product.images.map((image, index) => (
                <div 
                  key={index} 
                  className={`thumbnail ${mainImage === image ? 'active' : ''}`}
                  onClick={() => handleImageChange(image)}
                >
                  <img src={image} alt={`${product.name} ${index + 1}`} />
                </div>
              ))}
            </div>
          </div>

          <div className="product-info">
            <div className="product-header">
              <h1 className="product-name">{product.name}</h1>
              <div className="product-rating">
                {[...Array(5)].map((_, i) => (
                  <i 
                    key={i} 
                    className={`${i < Math.floor(product.rating) ? 'fas' : i < product.rating ? 'fas fa-star-half-alt' : 'far'} fa-star`}
                  ></i>
                ))}
                <span className="rating-text">
                  {product.rating} ({product.reviewCount} reseñas)
                </span>
              </div>
            </div>

            <div className="product-price">
              {product.discount > 0 && (
                <span className="original-price">
                  ${product.originalPrice.toFixed(2)}
                </span>
              )}
              <span className="current-price">
                ${product.price.toFixed(2)}
              </span>
              {product.discount > 0 && (
                <span className="discount-badge">
                  -{product.discount}%
                </span>
              )}
            </div>

            <div className="product-description">
              <p>{product.description}</p>
            </div>

            <div className="product-options">
              {product.colors.length > 0 && (
                <div className="option-section">
                  <h3 className="option-title">Color:</h3>
                  <div className="color-options">
                    {product.colors.map((color, index) => (
                      <button 
                        key={index} 
                        className={`color-option ${selectedColor === color ? 'active' : ''}`}
                        onClick={() => handleColorChange(color)}
                      >
                        {color}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              <div className="option-section">
                <h3 className="option-title">Cantidad:</h3>
                <div className="quantity-selector">
                  <button 
                    className="quantity-btn" 
                    onClick={decrementQuantity}
                    disabled={quantity <= 1}
                  >
                    <i className="fas fa-minus"></i>
                  </button>
                  <input 
                    type="number" 
                    min="1" 
                    max={product.stock} 
                    value={quantity} 
                    onChange={handleQuantityChange} 
                    className="quantity-input"
                  />
                  <button 
                    className="quantity-btn" 
                    onClick={incrementQuantity}
                    disabled={quantity >= product.stock}
                  >
                    <i className="fas fa-plus"></i>
                  </button>
                </div>
                <p className="stock-info">
                  {product.stock > 0 
                    ? `${product.stock} unidades disponibles` 
                    : 'Producto agotado'}
                </p>
              </div>
            </div>

            <div className="product-actions">
              <button 
                className="btn btn-primary add-to-cart-btn"
                onClick={handleAddToCart}
                disabled={product.stock === 0}
              >
                <i className="fas fa-shopping-cart"></i>
                Añadir al Carrito
              </button>
              <button className="btn btn-secondary wish-btn">
                <i className="fas fa-heart"></i>
                Añadir a Favoritos
              </button>
            </div>

            <div className="product-features">
              <h3 className="features-title">Características:</h3>
              <ul className="features-list">
                {product.features.map((feature, index) => (
                  <li key={index} className="feature-item">
                    <i className="fas fa-check"></i>
                    {feature}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default ProductDetailPage;
