import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './HeroSlider.css';

const HeroSlider = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const slides = [
    {
      id: 1,
      category: 'Trabajo ligero, LED, blanco',
      title: 'Styles Accessories &',
      subtitle: 'New Table Lamp',
      buttonText: 'Shop Now',
      link: '/producto/1',
      imagePlaceholder: true
    },
    {
      id: 2,
      category: 'Muebles, Decoración',
      title: 'Diseños Exclusivos',
      subtitle: 'Artesanía Premium',
      buttonText: 'Ver Catálogo',
      link: '/catalogo',
      imagePlaceholder: true
    }
  ];

  const handleDotClick = (index) => {
    setActiveSlide(index);
  };

  return (
    <section className="hero-slider">
      <div className="container hero-container">
        <div className="slide-content-wrapper">
          <div className="slide-content">
            <p className="slide-category">{slides[activeSlide].category}</p>
            <h1 className="slide-title">
              {slides[activeSlide].title}
              <span className="slide-subtitle">{slides[activeSlide].subtitle}</span>
            </h1>
            <Link to={slides[activeSlide].link} className="slide-button">
              {slides[activeSlide].buttonText}
            </Link>
          </div>
          
          <div className="slide-image">
            {/* Espacio para la imagen que se añadirá posteriormente */}
            <div className="image-placeholder"></div>
          </div>
        </div>
        
        <div className="slider-navigation">
          {slides.map((slide, index) => (
            <button
              key={slide.id}
              className={`dot ${index === activeSlide ? 'active' : ''}`}
              onClick={() => handleDotClick(index)}
              aria-label={`Ir a slide ${index + 1}`}
            ></button>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HeroSlider;
