import React, { useState, useEffect } from 'react';
import './App.css';
import ProductCard from './components/ProductCard';

function App() {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [newProducts, setNewProducts] = useState([]);
  const [catalogProducts, setCatalogProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const [featuredRes, newRes, catalogRes] = await Promise.all([
          fetch('http://localhost:8000/api/v1/products/featured/'),
          fetch('http://localhost:8000/api/v1/products/new/'),
          fetch('http://localhost:8000/api/v1/products/catalog/')
        ]);

        const [featured, newProds, catalog] = await Promise.all([
          featuredRes.json(),
          newRes.json(),
          catalogRes.json()
        ]);

        setFeaturedProducts(featured);
        setNewProducts(newProds);
        setCatalogProducts(catalog);
        setLoading(false);
      } catch (err) {
        console.error('Error al cargar los productos:', err);
        setError('Error al cargar los productos');
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <div className="container">Cargando productos...</div>;
  }

  if (error) {
    return <div className="container error">{error}</div>;
  }

  return (
    <div className="container">
      {featuredProducts.length > 0 && (
        <section className="products-section">
          <h2>Productos Destacados</h2>
          <div className="products-grid">
            {featuredProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </section>
      )}

      {newProducts.length > 0 && (
        <section className="products-section">
          <h2>Productos Nuevos</h2>
          <div className="products-grid">
            {newProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </section>
      )}

      {catalogProducts.length > 0 && (
        <section className="products-section">
          <h2>Cat√°logo Completo</h2>
          <div className="products-grid">
            {catalogProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </section>
      )}
    </div>
  );
}

export default App;
