import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './HeroSlider.css';
import pinturas from './assets/pinturas.jpeg';
import ofera from './assets/hero-2.jpg';

const HeroSlider = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const slides = [
    {
      id: 1,
      category: 'Artesanía de Calidad',
      title: 'Muebles Artesanales',
      subtitle: 'Hechos a Mano',
      buttonText: 'Ver Catálogo',
      link: '/catalogo',
      image: hero1
    },
    {
      id: 2,
      category: 'Diseño Exclusivo',
      title: 'Iluminación',
      subtitle: 'Moderna y Elegante',
      buttonText: 'Descubrir',
      link: '/catalogo',
      image: hero2
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveSlide((current) => (current + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const handleDotClick = (index) => {
    setActiveSlide(index);
  };

  return (
    <section className="hero-slider">
      <div className="hero-container">
        <div className="slide-content-wrapper">
          <div className="slide-content">
            <p className="slide-category">{slides[activeSlide].category}</p>
            <h1 className="slide-title">
              {slides[activeSlide].title}
              <span className="slide-subtitle">{slides[activeSlide].subtitle}</span>
            </h1>
            <Link to={slides[activeSlide].link} className="slide-button">
              {slides[activeSlide].buttonText}
            </Link>
          </div>
          
          <div className="slide-image">
            <img 
              src={slides[activeSlide].image} 
              alt={slides[activeSlide].title} 
              className="image-placeholder"
            />
          </div>
        </div>
        
        <div className="slider-navigation">
          {slides.map((slide, index) => (
            <button
              key={slide.id}
              className={`dot ${index === activeSlide ? 'active' : ''}`}
              onClick={() => handleDotClick(index)}
              aria-label={`Ir a slide ${index + 1}`}
            ></button>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HeroSlider;
