import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import ProductFilter from '../components/ProductFilter';
import ProductSearch from '../components/ProductSearch';
import ProductCard from '../components/ProductCard';
import './CatalogPage.css';

const CatalogPage = () => {
  // Estado para almacenar todos los productos
  const [products, setProducts] = useState([]);
  // Estado para almacenar los productos filtrados
  const [filteredProducts, setFilteredProducts] = useState([]);
  // Estado para almacenar los parámetros de búsqueda y filtrado
  const [searchParams, setSearchParams] = useState({
    searchTerm: '',
    sortOption: 'relevance',
    filters: {
      priceRange: [0, 1000],
      categories: []
    }
  });

  // Cargar productos de muestra (en un caso real, se cargarían desde una API)
  useEffect(() => {
    // Datos de muestra
    const sampleProducts = [
      {
        id: 1,
        name: 'Lámpara Artesanal de Madera',
        category: 'Iluminación',
        image: '/product1.jpg',
        price: 89.99,
        originalPrice: 119.99,
        discount: 25,
        rating: 4.5,
        reviewCount: 12,
        isNew: false
      },
      {
        id: 2,
        name: 'Mesa de Centro Rústica',
        category: 'Muebles',
        image: '/product2.jpg',
        price: 249.99,
        originalPrice: 249.99,
        discount: 0,
        rating: 5,
        reviewCount: 8,
        isNew: false
      },
      {
        id: 3,
        name: 'Estantería Modular',
        category: 'Almacenamiento',
        image: '/product3.jpg',
        price: 159.99,
        originalPrice: 199.99,
        discount: 20,
        rating: 4,
        reviewCount: 6,
        isNew: false
      },
      {
        id: 4,
        name: 'Silla de Comedor',
        category: 'Muebles',
        image: '/product4.jpg',
        price: 79.99,
        originalPrice: 79.99,
        discount: 0,
        rating: 4.5,
        reviewCount: 10,
        isNew: false
      },
      {
        id: 5,
        name: 'Lámpara de Pie Moderna',
        category: 'Iluminación',
        image: '/product5.jpg',
        price: 129.99,
        originalPrice: 149.99,
        discount: 15,
        rating: 4,
        reviewCount: 3,
        isNew: true
      },
      {
        id: 6,
        name: 'Escritorio de Trabajo',
        category: 'Muebles',
        image: '/product6.jpg',
        price: 199.99,
        originalPrice: 229.99,
        discount: 10,
        rating: 5,
        reviewCount: 4,
        isNew: true
      },
      {
        id: 7,
        name: 'Juego de Comedor',
        category: 'Muebles',
        image: '/product7.jpg',
        price: 599.99,
        originalPrice: 599.99,
        discount: 0,
        rating: 4.5,
        reviewCount: 2,
        isNew: true
      },
      {
        id: 8,
        name: 'Espejo Decorativo',
        category: 'Decoración',
        image: '/product8.jpg',
        price: 69.99,
        originalPrice: 89.99,
        discount: 20,
        rating: 4,
        reviewCount: 5,
        isNew: true
      },
      {
        id: 9,
        name: 'Cojín Decorativo',
        category: 'Textiles',
        image: '/product9.jpg',
        price: 24.99,
        originalPrice: 29.99,
        discount: 15,
        rating: 4.5,
        reviewCount: 15,
        isNew: false
      },
      {
        id: 10,
        name: 'Organizador de Pared',
        category: 'Almacenamiento',
        image: '/product10.jpg',
        price: 45.99,
        originalPrice: 45.99,
        discount: 0,
        rating: 4,
        reviewCount: 7,
        isNew: false
      },
      {
        id: 11,
        name: 'Cortinas de Lino',
        category: 'Textiles',
        image: '/product11.jpg',
        price: 59.99,
        originalPrice: 79.99,
        discount: 25,
        rating: 4.5,
        reviewCount: 9,
        isNew: true
      },
      {
        id: 12,
        name: 'Lámpara Colgante',
        category: 'Iluminación',
        image: '/product12.jpg',
        price: 149.99,
        originalPrice: 149.99,
        discount: 0,
        rating: 5,
        reviewCount: 11,
        isNew: true
      }
    ];

    setProducts(sampleProducts);
    setFilteredProducts(sampleProducts);
  }, []);

  // Función para aplicar filtros y ordenación
  useEffect(() => {
    let result = [...products];

    // Aplicar filtro de búsqueda
    if (searchParams.searchTerm) {
      const term = searchParams.searchTerm.toLowerCase();
      result = result.filter(product => 
        product.name.toLowerCase().includes(term) || 
        product.category.toLowerCase().includes(term)
      );
    }

    // Aplicar filtro de categorías
    if (searchParams.filters.categories.length > 0) {
      result = result.filter(product => 
        searchParams.filters.categories.includes(product.category)
      );
    }

    // Aplicar filtro de rango de precios
    result = result.filter(product => 
      product.price >= searchParams.filters.priceRange[0] && 
      product.price <= searchParams.filters.priceRange[1]
    );

    // Aplicar ordenación
    switch (searchParams.sortOption) {
      case 'price-low':
        result.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        result.sort((a, b) => b.price - a.price);
        break;
      case 'newest':
        result.sort((a, b) => (a.isNew === b.isNew) ? 0 : a.isNew ? -1 : 1);
        break;
      case 'rating':
        result.sort((a, b) => b.rating - a.rating);
        break;
      default:
        // Por defecto, no se aplica ninguna ordenación
        break;
    }

    setFilteredProducts(result);
  }, [products, searchParams]);

  // Manejar la búsqueda y ordenación
  const handleSearch = ({ searchTerm, sortOption }) => {
    setSearchParams(prevParams => ({
      ...prevParams,
      searchTerm: searchTerm || prevParams.searchTerm,
      sortOption: sortOption || prevParams.sortOption
    }));
  };

  // Manejar el cambio de filtros
  const handleFilterChange = (filters) => {
    setSearchParams(prevParams => ({
      ...prevParams,
      filters
    }));
  };

  return (
    <div className="catalog-page">
      <Navbar />

      <div className="catalog-container container">
        <h1 className="catalog-title">Catálogo de Productos</h1>

        <ProductSearch onSearch={handleSearch} />

        <div className="catalog-content">
          <aside className="catalog-sidebar">
            <ProductFilter onFilterChange={handleFilterChange} />
          </aside>

          <main className="catalog-products">
            {filteredProducts.length === 0 ? (
              <div className="no-products-message">
                <p>No se encontraron productos que coincidan con tu búsqueda.</p>
                <p>Intenta con otros términos o ajusta los filtros.</p>
              </div>
            ) : (
              <div className="products-grid">
                {filteredProducts.map(product => (
                  <ProductCard key={product.id} product={product} />
                ))}
              </div>
            )}
          </main>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default CatalogPage;
