import React, { useState } from 'react';
import './ProductFilter.css';

const ProductFilter = ({ categories, selectedCategory, onCategoryChange }) => {
  const [priceRange, setPriceRange] = useState([0, 1000]);

  const handlePriceChange = (e, type) => {
    const value = parseInt(e.target.value);
    if (type === 'min') {
      setPriceRange([value, priceRange[1]]);
    } else {
      setPriceRange([priceRange[0], value]);
    }
  };

  const applyFilters = () => {
    onCategoryChange(selectedCategory);
  };

  const clearFilters = () => {
    setPriceRange([0, 1000]);
    onCategoryChange('');
  };

  return (
    <div className="product-filter">
      <h3 className="filter-title">Filtros</h3>
      
      <div className="filter-section">
        <h4 className="filter-subtitle">Categorías</h4>
        <div className="category-list">
          <button 
            className={`category-button ${!selectedCategory ? 'active' : ''}`}
            onClick={() => onCategoryChange('')}
          >
            Todas las categorías
          </button>
          {categories.map(category => (
            <button
              key={category.id}
              className={`category-button ${selectedCategory === category.slug ? 'active' : ''}`}
              onClick={() => onCategoryChange(category.slug)}
            >
              {category.name}
            </button>
          ))}
        </div>
      </div>

      <div className="filter-section">
        <h4 className="filter-subtitle">Rango de Precios</h4>
        <div className="price-inputs">
          <div className="price-input">
            <label>Mín:</label>
            <input 
              type="number" 
              min="0" 
              max={priceRange[1]} 
              value={priceRange[0]} 
              onChange={(e) => handlePriceChange(e, 'min')}
            />
          </div>
          <div className="price-input">
            <label>Máx:</label>
            <input 
              type="number" 
              min={priceRange[0]} 
              max="1000" 
              value={priceRange[1]} 
              onChange={(e) => handlePriceChange(e, 'max')}
            />
          </div>
        </div>
        <div className="price-slider">
          <input 
            type="range" 
            min="0" 
            max="1000" 
            value={priceRange[0]} 
            onChange={(e) => handlePriceChange(e, 'min')}
            className="range-min"
          />
          <input 
            type="range" 
            min="0" 
            max="1000" 
            value={priceRange[1]} 
            onChange={(e) => handlePriceChange(e, 'max')}
            className="range-max"
          />
        </div>
      </div>

      <div className="filter-actions">
        <button className="btn btn-primary" onClick={applyFilters}>
          Aplicar Filtros
        </button>
        <button className="btn btn-secondary" onClick={clearFilters}>
          Limpiar Filtros
        </button>
      </div>
    </div>
  );
};

export default ProductFilter;
