import React, { useState, useEffect } from 'react';
import './HeroSlider.css';

const HeroSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      image: '/slide1.jpg',
      title: 'Artesanía de Calidad',
      description: 'Descubre nuestros productos hechos a mano con materiales de primera calidad',
      buttonText: 'Ver Catálogo',
      buttonLink: '/catalogo'
    },
    {
      image: '/slide2.jpg',
      title: 'Nuevas Colecciones',
      description: 'Explora nuestras nuevas colecciones con diseños exclusivos',
      buttonText: 'Ver Novedades',
      buttonLink: '/catalogo?categoria=nuevos'
    },
    {
      image: '/slide3.jpg',
      title: 'Diseños Personalizados',
      description: 'Crea tu producto a medida con nuestro servicio de personalización',
      buttonText: 'Personalizar',
      buttonLink: '/personalizar'
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide(prevSlide => 
        prevSlide === slides.length - 1 ? 0 : prevSlide + 1
      );
    }, 5000);

    return () => clearInterval(interval);
  }, [slides.length]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide(prevSlide => 
      prevSlide === slides.length - 1 ? 0 : prevSlide + 1
    );
  };

  const prevSlide = () => {
    setCurrentSlide(prevSlide => 
      prevSlide === 0 ? slides.length - 1 : prevSlide - 1
    );
  };

  return (
    <div className="hero-slider">
      <div className="slides-container">
        {slides.map((slide, index) => (
          <div 
            key={index} 
            className={`slide ${index === currentSlide ? 'active' : ''}`}
            style={{ backgroundImage: `url(${slide.image})` }}
          >
            <div className="slide-content container">
              <h1 className="slide-title">{slide.title}</h1>
              <p className="slide-description">{slide.description}</p>
              <a href={slide.buttonLink} className="btn btn-primary">{slide.buttonText}</a>
            </div>
          </div>
        ))}
      </div>

      <button className="slider-nav prev" onClick={prevSlide}>
        <i className="fas fa-chevron-left"></i>
      </button>
      <button className="slider-nav next" onClick={nextSlide}>
        <i className="fas fa-chevron-right"></i>
      </button>

      <div className="slider-dots">
        {slides.map((_, index) => (
          <button 
            key={index} 
            className={`slider-dot ${index === currentSlide ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
          ></button>
        ))}
      </div>
    </div>
  );
};

export default HeroSlider;
